import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

// Register necessary components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function CustomerLifetimeValueChart() {
  const [chartData, setChartData] = useState(null);
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    const fetchCustomerLifetimeValue = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/shopify/customer-lifetime-value`, {
          params: { page, limit: 10, search }
        });
        const data = response.data || [];

        if (data.length === 0) {
          console.warn('No customer lifetime value data found');
          setChartData({
            labels: [],
            datasets: [
              {
                label: 'Total Spent',
                data: [],
                backgroundColor: 'rgba(75,192,192,0.6)',
                borderColor: 'rgba(75,192,192,1)',
              },
            ],
          });
          return;
        }

        const labels = data.map((item) => `${item.firstName} ${item.lastName}`);
        const values = data.map((item) => item.totalSpent);

        setChartData({
          labels,
          datasets: [
            {
              label: 'Total Spent',
              data: values,
              backgroundColor: 'rgba(75,192,192,0.6)',
              borderColor: 'rgba(75,192,192,1)',
            },
          ],
        });

        // Assuming pagination info is included in response
        const pagination = response.data.pagination || {};
        setTotalPages(pagination.totalPages || 1);
      } catch (error) {
        console.error('Error fetching customer lifetime value data', error);
      }
    };

    fetchCustomerLifetimeValue();
  }, [page, search]);

  const handleSearchChange = (event) => {
    setSearch(event.target.value);
    setPage(1); // Reset to first page on search
  };

  const handlePageChange = (newPage) => {
    setPage(newPage);
  };

  if (!chartData) {
    return <p>Loading chart data...</p>;
  }

  return (
    <div>
      <h2>Customer Lifetime Value</h2>
      <input 
        type="text" 
        placeholder="Search by name or email" 
        value={search} 
        onChange={handleSearchChange} 
      />
      <Bar data={chartData} />
      <div>
        <button onClick={() => handlePageChange(page - 1)} disabled={page === 1}>
          Previous
        </button>
        <span>Page {page} of {totalPages}</span>
        <button onClick={() => handlePageChange(page + 1)} disabled={page === totalPages}>
          Next
        </button>
      </div>
    </div>
  );
}

export default CustomerLifetimeValueChart;
