import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    LineElement,  // Import LineElement for Line charts
    PointElement, // Import PointElement for Line charts
    Title,
    Tooltip,
    Legend
} from 'chart.js';

// Register necessary components
ChartJS.register(
    CategoryScale,
    LinearScale,
    LineElement,
    PointElement,
    Title,
    Tooltip,
    Legend
);

const ChartContainer = styled.div`
  width: 80%;
  margin: 0 auto;
  padding: 2rem;
  background-color: #ffffff;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const ChartTitle = styled.h2`
  text-align: center;
  color: #333;
  margin-bottom: 1.5rem;
`;

const LoadingMessage = styled.p`
  text-align: center;
  color: #666;
  font-size: 1.2rem;
`;

const BackButton = styled.button`
  display: block;
  margin: 1rem auto;
  padding: 0.5rem 1rem;
  font-size: 1rem;
  color: #fff;
  background-color: #007bff;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  
  &:hover {
    background-color: #0056b3;
  }
`;

function NewCustomersChart() {
  const [chartData, setChartData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchNewCustomersData = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/shopify/new-customers`);
        const data = response.data || [];

        if (data.length === 0) {
          console.warn('No new customers data found');
          return;
        }

        const labels = data.map((item) => `${item._id.month}/${item._id.year}`);
        const values = data.map((item) => item.count);

        setChartData({
          labels,
          datasets: [
            {
              label: 'New Customers',
              data: values,
              fill: false,
              backgroundColor: 'rgba(255,159,64,0.6)',
              borderColor: 'rgba(255,159,64,1)',
            },
          ],
        });
      } catch (error) {
        console.error('Error fetching new customers data', error);
      }
    };

    fetchNewCustomersData();
  }, []);

  if (!chartData) {
    return <LoadingMessage>Loading chart data...</LoadingMessage>;
  }

  return (
    <ChartContainer>
      <BackButton onClick={() => navigate('/')}>Back to Home</BackButton>
      <ChartTitle>New Customers Over Time</ChartTitle>
      <Line data={chartData} />
    </ChartContainer>
  );
}

export default NewCustomersChart;
